package lab2part1;

public class Ex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		for(i=0;i<10;i++)
		{
		System.out.println(i);
		}
		
		
		for(int j=4;j<12;j++)
		{
		System.out.print(j+",");
		}
		System.out.println(" ");
		
		
		for(int e=10;e<=19;e+=3)
		{
		System.out.print(e+",");
		}
		System.out.println(" ");
				
		
		for(int h=1;h<=16;h+=3)
		{
		System.out.print(h+",");
		}
		System.out.println(" ");
		
		
		for(int p=2;p<=12;p+=2)
		{
		System.out.print(p+",");
		}
		System.out.println(" ");
		
		
		int f;
		for (int z=1;z<=10;z++)
		{
		f=z*z;
		System.out.print(f+",");
		}
		System.out.println();
		
		
		for(int o=-10;o<=10;o+=2)
		{
		System.out.print(o+",");
		}
		System.out.println(" ");
		
		
		for(int n=-20;n<=20;n+=5)
		{
		if (n!=0) {
		System.out.print(n+",");}
		}
		System.out.println(" ");
	}
	
	

}
